query:
/* OAS v2 Changelog */
(
$specOps := $.{
"info":info,
"operations":**.operationId,
"paths": paths
};

$newOps := $specOps[1].operations[
    $ in $specOps[0].operations ? null : $
];

$newPaths := $specOps[1].paths[
    $ in $specOps[0].paths ? null : $
];

$newPathDetails := $newPaths.*#$index.{
  "$index": $index,
  "path": $keys($newPaths)[$index],
  "operations": **.operationId.{
    "operationid": %.operationId,
    "description": %.description,
    "tags":%.tags
  }
};

/* Final Output */
{
    "stats": {
        "oldVersion": $[0].info.version,
        "newVersion": $[1].info.version,
        "totalOperations": $count($specOps[1].operations),
        "newOperations": $count($newOps),
        "newPaths": $count($keys($newPaths))
        },
    "details": $newPathDetails
};
)
